import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Cursos.css';
import { useFont } from './context/ZoomContext';
import curso1 from '../assets/curso1.jpeg';
import curso2 from '../assets/curso2.jpeg';
import curso3 from '../assets/curso3.jpeg';
import db from '../db.json'; // Importe o arquivo db.json aqui

const Cursos = () => {
    const navigate = useNavigate();
    const { fontSize } = useFont();

    const cursos = [
        { img: curso1, title: 'Curso 1', description: 'Descrição do Curso 1', name: 'aplicativos' },
        { img: curso2, title: 'Curso 2', description: 'Descrição do Curso 2', name: 'whatsapp' },
        { img: curso3, title: 'Curso 3', description: 'Descrição do Curso 3', name: 'instagram' }
    ];

    const handleSubmit = (event, name) => {
        event.preventDefault();
        const curso = findCursoByName(name);
        if (curso) {
            navigate(curso[0].link); // Navega para o primeiro item encontrado com o nome correspondente
        } else {
            console.error(`Curso com nome '${name}' não encontrado.`);
        }
    };

    // Função para encontrar o curso pelo nome no db.json
    const findCursoByName = (name) => {
        return db[name]; // Retorna o array de cursos com o nome correspondente
    };

    return (
        <div className="config-container" style={{ fontSize: `${fontSize}px` }}>
            <h1>Central de ensino help idoso</h1>
            <div className="card-container">
                {cursos.map((curso) => (
                    <div className="card" key={curso.name}>
                        <img src={curso.img} alt={curso.title} className="card-image" />
                        <h2>{curso.title}</h2>
                        <p>{curso.description}</p>
                        <button type="button" onClick={(event) => handleSubmit(event, curso.name)}>Começar</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Cursos;
