import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useFont } from './context/ZoomContext';
import './Curso.css';
import { data } from '../db.json';

const Curso = () => {
  const { id } = useParams();
  const [videoUrl, setVideoUrl] = useState('');
  const [pergunta, setPergunta] = useState('');
  const [resposta, setResposta] = useState('');
  const { fontSize } = useFont();
  const [aulas, setAulas] = useState([]);

  useEffect(() => {
    if (id === '1') {
      setAulas(data.aplicativos);
    } else if (id === '2') {
      setAulas(data.instagram);
    } else if (id === '3') {
      setAulas(data.whatsapp);
    }
  }, [id]);

  const handleNext = (index) => {
    const aula = aulas[index];
    console.log(`Próxima aula: ${aula.nome}`);
    setVideoUrl(aula.link); // Define o URL do vídeo selecionado
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Pergunta:', pergunta);
    console.log('Resposta:', resposta);
    setPergunta('');
    setResposta('');
  };

  return (
    <div className="painel-container" style={{ fontSize: `${fontSize}px` }}>
      <div className="video-section">
        <h2>Reprodução de Vídeo</h2>
        {videoUrl ? (
          <video controls className="video-player">
            <source src={videoUrl} type="video/mp4" />
            Seu navegador não suporta a reprodução de vídeo.
          </video>
        ) : (
          <p>Clique em uma aula para iniciar o vídeo.</p>
        )}
      </div>
      <div className="content-container">
        <div className="lessons-list">
          <h2>Lista de Aulas</h2>
          {aulas.map((aula, index) => (
            <button key={aula.id} onClick={() => handleNext(index)}>
              {aula.nome}
            </button>
          ))}
        </div>
        <div className="comments-section">
          <h2>Comentários</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="pergunta">Pergunta:</label>
              <input
                type="text"
                id="pergunta"
                value={pergunta}
                onChange={(e) => setPergunta(e.target.value)}
                required
              />
            </div>
            <div>
              <label htmlFor="resposta">Resposta:</label>
              <input
                type="text"
                id="resposta"
                value={resposta}
                onChange={(e) => setResposta(e.target.value)}
                required
              />
            </div>
            <button type="submit">Enviar</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Curso;
