import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom'; // Importando Link
import { useFont } from './context/ZoomContext';
import './Curso.css';
import db from '../db.json'; // Importando o db.json

const Curso = () => {
  const { id } = useParams();
  const [videoId, setVideoId] = useState(''); // Estado para armazenar o ID do vídeo do YouTube
  const [pergunta, setPergunta] = useState('');
  const [resposta, setResposta] = useState('');
  const { fontSize } = useFont();

  let aulas = [];
  if (id === '1') {
    aulas = db.aplicativos;
  } else if (id === '2') {
    aulas = db.instagram;
  } else if (id === '3') {
    aulas = db.whatsapp;
  }

  useEffect(() => {
    // Carregar o primeiro vídeo da lista de aulas ao montar o componente
    if (aulas.length > 0) {
      setVideoId(aulas[0].link);
    }
  }, [aulas]);

  const handleNext = (index) => {
    const aula = aulas[index];
    console.log(`Próxima aula: ${aula.nome}`);
    setVideoId(aula.link); // Utiliza o link diretamente como videoId
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Pergunta:', pergunta);
    console.log('Resposta:', resposta);
    setPergunta('');
    setResposta('');
  };

  return (
    <div className="painel-container" style={{ fontSize: `${fontSize}px` }}>
      <div className="video-section">
        <h2>Reprodução de Vídeo</h2>
        {videoId ? (
          <div className="video-wrapper">
            <iframe
              title="YouTube Video"
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${videoId}`}
              frameBorder="0"
              allowFullScreen
            ></iframe>
          </div>
        ) : (
          <p>Clique em uma aula para iniciar o vídeo.</p>
        )}
      </div>
      <div className="content-container">
        <div className="lessons-list">
          <h2>Lista de Aulas</h2>
          {aulas.map((aula, index) => (
            <button key={aula.id} onClick={() => handleNext(index)}>
              {aula.nome}
            </button>
          ))}
        </div>
        <div className="comments-section">
          <h2>Comentários</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="pergunta">Pergunta:</label>
              <input
                type="text"
                id="pergunta"
                value={pergunta}
                onChange={(e) => setPergunta(e.target.value)}
                required
              />
            </div>
            <div>
              <label htmlFor="resposta">Resposta:</label>
              <input
                type="text"
                id="resposta"
                value={resposta}
                onChange={(e) => setResposta(e.target.value)}
                required
              />
            </div>
            <button type="submit">Enviar</button>
          </form>
        </div>
      </div>
      {/* Botão para voltar à lista de cursos */}
      <Link to="/cursos" className="btn-voltar">
        Voltar aos Cursos
      </Link>
    </div>
  );
};

export default Curso;
