import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useFont } from './context/ZoomContext';
import './Curso.css';
import db from '../db.json';

const Curso = () => {
  const { id } = useParams();
  const [videoId, setVideoId] = useState('');
  const [pergunta, setPergunta] = useState('');
  const [resposta, setResposta] = useState('');
  const { fontSize } = useFont();

  let aulas = [];
  let tipoCurso = '';

  if (id === '1') {
    aulas = db.aplicativos;
    tipoCurso = 'aplicativos';
  } else if (id === '2') {
    aulas = db.instagram;
    tipoCurso = 'instagram';
  } else if (id === '3') {
    aulas = db.whatsapp;
    tipoCurso = 'whatsapp';
  }

  useEffect(() => {
    if (aulas.length > 0) {
      setVideoId(aulas[0].link);
    }
  }, [aulas]);

  const handleNext = (index) => {
    const aula = aulas[index];
    console.log(`Próxima aula: ${aula.nome}`);
    setVideoId(aula.link);
  };

  const handleProximoVideo = () => {
    // Encontrar o índice do vídeo atual
    const currentIndex = aulas.findIndex(aula => aula.link === videoId);
    // Calcular o próximo índice
    const nextIndex = (currentIndex + 1) % aulas.length;
    // Definir o próximo vídeo
    const proximoVideo = aulas[nextIndex];
    setVideoId(proximoVideo.link);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Pergunta:', pergunta);
    console.log('Resposta:', resposta);
    setPergunta('');
    setResposta('');
  };

  return (
    <div className="painel-container" style={{ fontSize: `${fontSize}px` }}>
      <div className="video-section">
        {videoId && (
          <div className="video-wrapper">
            <iframe
              title="YouTube Video"
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${videoId}`}
              frameBorder="0"
              allowFullScreen
            ></iframe>
          </div>
        )}
        {videoId && (
          <div className="video-description">
            <h2>Descrição do Vídeo</h2>
            <p>{aulas.find(aula => aula.link === videoId)?.descrição}</p>
          </div>
        )}
        {videoId && (
          <button className="btn-proximo-video" onClick={handleProximoVideo}>
            Próximo Vídeo
          </button>
        )}
      </div>
      <div className="content-container">
        <div className="lessons-list">
          <h2>Lista de Aulas</h2>
          {aulas.map((aula, index) => (
            <button key={aula.id} onClick={() => handleNext(index)}>
              {aula.nome}
            </button>
          ))}
        </div>
        <div className="comments-section">
          <h2>Comentários</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="pergunta">Pergunta:</label>
              <input
                type="text"
                id="pergunta"
                value={pergunta}
                onChange={(e) => setPergunta(e.target.value)}
                required
              />
            </div>
            <div>
              <label htmlFor="resposta">Resposta:</label>
              <input
                type="text"
                id="resposta"
                value={resposta}
                onChange={(e) => setResposta(e.target.value)}
                required
              />
            </div>
            <button type="submit">Enviar</button>
          </form>
        </div>
      </div>
      <Link to="/cursos" className="btn-voltar">
        Voltar aos Cursos
      </Link>
    </div>
  );
};

export default Curso;
